<template>
  <div class="myDiv">
    <table>
      <thead>
        <th>ID</th>
        <th>NAME</th>
        <th>STOCK</th>
        <th>PRICE</th>
        <th>IMAGE</th>
      </thead>
      <tr v-for="product in products" :key="product.id" @click="addProductOnCart(product)">
        <td>
          {{ product.id }}
        </td>
        <td>
          {{ product.name }}
        </td>
        <td>
          {{ product.stock }}
        </td>
        <td>
          {{ product.price }}
        </td>
        <td>
          <img :src="product.img" alt="">
        </td>
      </tr>
    </table>    
  </div>
</template>
<script>
export default {
  data() {
    return {
      products: [
        {
          id: 1,
          name: 'Shampoo1',
          img: 'data:image/jpeg;base64,',
          stock: 3,
          price: 3429.45
        },
        {
          id: 2,
          name: 'Shampoo2',
          img: 'data:image/jpeg;base64,',
          stock: 5,
          price: 234.54
        },
        {
          id: 3,
          name: 'Shampoo3',
          img: 'data:image/jpeg;base64,',
          stock: 1,
          price: 58.95
        }
      ]
    }
  },

  methods: {
    addProductOnCart(product) {
      if (product.stock <= 0) {
        alert('Can\'t add a product without stock');
        return;
      }

      this.$emit('productAdded', product);
    }
  }
}
</script>
