  img {
    max-width: 100px;
  }

  tr, table, th, td {
    border: 2px solid black;
    border-collapse: collapse;
  }

  table {
     width: 50%;
     margin: auto;
  }

  tr {
    cursor: pointer;
  }

  tr:hover {
    background-color: grey;
  }
</style>